import { Component } from "@angular/core";
import { Course } from "../model/course.model";
import { Model } from "../model/repository.model";
import { MODES, SharedState } from "./sharedState.service";
@Component({
  selector: "pTable",
  templateUrl: "table.component.html"
})
export class TableComponent {
  constructor(private model: Model, private state: SharedState) { }

  getCourse(key: number): Course | undefined {
    return this.model.getCourse(key);
  }
  getCourses(): Course[] {
    return this.model.getCourses();
  }
  deleteCourse(key?: number) {
    if (key != undefined) {
      this.model.deleteCourse(key);
    }
  }
  editCourse(key?: number) {
    this.state.update(MODES.EDIT, key)
  }
  createCourse() {
    this.state.update(MODES.CREATE);
  }
}
<table class="table table-sm table-bordered table-striped">
  <thead>
    <tr>
      <th>ID</th>
      <th>Title</th>
      <th>Seat Capacity</th>
      <th>Price</th>
      <th>Instructor</th>
      <th></th>
    </tr>
  </thead>
  <tbody>
    <tr *ngFor="let item of getCourses()">
      <td>{{item.id}}</td>
      <td>{{item.title}}</td>
      <td>{{item.seatCapacity}}</td>
      <td>{{item.price | currency:"USD"}}</td>
      <td>{{item.instructorName }}</td>
      <td class="text-center">
        <button class="btn btn-danger btn-sm m-1"
                (click)="deleteCourse(item.id)">
          Delete
        </button>
        <button class="btn btn-warning btn-sm" (click)="editCourse(item.id)">
          Edit
        </button>
      </td>
    </tr>
  </tbody>
</table>
<button class="btn btn-primary mt-1" (click)="createCourse()">
  Create New Course
</button>
