import React, { Component } from "react";

export class SupplierEditor extends Component {

    constructor(props) {
        super(props);
        this.state = {
            formData: {
                id: props.supplier.id || "",
                name: props.supplier.name || "",
                city: props.supplier.city || "",
                products: props.supplier.products || [],
            }
        }
    }

    handleChange = (ev) => {
        ev.persist();
        this.setState(state =>
            state.formData[ev.target.name] = ev.target.name === "products" ? ev.target.value.split(", ") : ev.target.value);
    }

    handleClick = () => {
        this.props.saveCallback(
            {
                ...this.state.formData,
                products: this.state.formData.products.map(val => val)
            });
    }

    render() {
        return <div className="m-2">
            <div className="form-group">
                <label>ID</label>
                <input className="form-control" name="id"
                    disabled
                    value={this.state.formData.id}
                    onChange={this.handleChange} />
            </div>
            <div className="form-group">
                <label>Name</label>
                <input className="form-control" name="name"
                    value={this.state.formData.name}
                    onChange={this.handleChange} />
            </div>
            <div className="form-group">
                <label>City</label>
                <input className="form-control" name="city"
                    value={this.state.formData.city}
                    onChange={this.handleChange} />
            </div>

            <div className="form-group">
                <label>Products</label>
                <input className="form-control" name="products"
                    value={this.state.formData.products}
                    onChange={this.handleChange} />
            </div>

            <div className="text-center">
                <button className="btn btn-primary m-1" onClick={this.handleClick}>
                    Save
                </button>
                <button className="btn btn-secondary"
                    onClick={this.props.cancelCallback}>
                    Cancel
                </button>
            </div>
        </div>
    }
}
